    private void txtCadastrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_txtCadastrarActionPerformed
        // TODO add your handling code here:
        String nome, endereco, cep, telefone, estado, email, dataNasc;
        nome = txtNome.getText();
        endereco = txtEndereco.getText();
        cep = txtCep.getText();
        telefone = txtTelefone.getText();
        estado = txtEstado.getText();
        email = txtEmail.getText();
        dataNasc = txtDataNascimento.getText();
        
        if(nome.isEmpty() || endereco.isEmpty() || cep.isEmpty() || telefone.isEmpty() || estado.isEmpty() || email.isEmpty() || dataNasc.isEmpty()){
            JOptionPane.showMessageDialog(null, "Preencha todos os campos para realizar o cadastro");
        }else{
            JOptionPane.showMessageDialog(null, "Nome: " + nome + "\n\nEndereco: " + endereco + "\n\nCEP: " + cep + "\n\nCidade: " + telefone + "\n\nEstado: " + estado + "\n\nEmail: " + email + "\n\nData de Nascimento: " + dataNasc );
        }
        
    }//GEN-LAST:event_txtCadastrarActionPerformed

